package com.squarepolka.portablephoto.entity;public class MediaFile extends AbstractMedia{	public static int TypePhoto = 0;	public static int TypeVideo = 1;	public static int TypeUnknown = 2;		public String name;	public MediaFolder parentFolder;		public MediaFile(String name, String path, MediaFolder parentFolder)	{		this.name = name;		this.path = path;		this.parentFolder = parentFolder;	}		public int type()	{		String lowerCaseName = name.toLowerCase();				if (lowerCaseName.contains("jpg") || lowerCaseName.contains("jpeg") || lowerCaseName.contains("dng") || lowerCaseName.contains("nef"))		{			return TypePhoto;		}		else if (lowerCaseName.contains("avi") || lowerCaseName.contains("mov") || lowerCaseName.contains("mp4") || lowerCaseName.contains("lrv") || lowerCaseName.contains("thm"))		{			return TypeVideo;		}		else		{			return TypeUnknown;		}	}}