package com.squarepolka.portablephoto.service;import org.springframework.stereotype.Service;import com.squarepolka.portablephoto.entity.api.StatusResponse;import com.squarepolka.portablephoto.persistence.MemoryStore;@Servicepublic class StatusService{	public StatusResponse getStatus()	{		StatusResponse response = new StatusResponse();		response.folderQueueSize = MemoryStore.mediaFolderQueue.size();		response.messages = MemoryStore.messages;		return response;	}}